import React from 'react'
import { Table, Button, Container } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useListEmployeeQuery } from '../employeeApi/EmployeeApiSlice'
import { useSelector } from 'react-redux'
import { selectEmployeeById } from '../employeeApi/EmployeeApiSlice'
//import Employee from './Employee'
import './listEmployee.css'

const ListEmployee = () => {
  const { data: employees, isLoading, isSuccess, isError } = useListEmployeeQuery()
  const employee = useSelector(state => selectEmployeeById(state,employeeId))
  let content;

  if (isLoading) {
    content = <p>Loading</p>
  }

  if (isError) {
    content = <p>Error Occured</p>
  }

  if (isSuccess) {

    const { ids, entities } = employees
    console.log("ids:", ids)
    console.log("entities:", entities)

    const tableContent = ids?.length ? ids.map(employeeId => <Employee key={employeeId} employeeId={employeeId} />) : null;

    content = (
      <Container>
        <div>
          <h3 className="HomeTitle">Employee List</h3>
          <Link to="/employees/addemployee" className="AddEmployeeLink"><Button>Add Employee</Button></Link>
          <Table striped bordered hover>
            <thead>
              <tr>
                {/* <th>Id</th> */}
                <th>Name</th>
                <th>User Name</th>
                <th>Email</th>
                <th>Contact Number</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
             // {tableContent}
             <tr>
          {/* <td>1</td> */}
          <td>{employee.name}</td>
          <td>{employee.username}</td>
          <td>{employee.email}</td>
          <td>{employee.number}</td>
          <td><Button>Edit</Button><Button>Delete</Button></td>
        </tr>
            </tbody>
          </Table>

        </div>
      </Container>
    )

  }

  return content
}

export default ListEmployee
