import React, { useState } from 'react'
import {Button, Form} from 'react-bootstrap';
import { employeeApiSlice } from '../employeeApi/EmployeeApiSlice';
import ListEmployee from '../listEmployee/ListEmployee';
import './addEmployee.css'

const AddEmployee = () => {

  const[name, setName] = useState('');
  const[username, setUsername] = useState('');
  const[email, setEmail] = useState('');
  const[number, setNumber] = useState('');

  const handleSubmit =async (e) => {
    e.preventDefault();
    await  ListEmployee()
  }

  return (
    <div className='AddEmployeeContainer'>
      <h3 className="AddEmployeeTitle">Add Employee</h3>
      <Form className='AddEmployeeForm pb-6'>
      <Form.Group className="mb-3" controlId="formBasicEmail" >
        <Form.Label>Name</Form.Label>
        <Form.Control type="email" placeholder="Enter name" name='name' onChange={onNameChange}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Username</Form.Label>
        <Form.Control type="email" placeholder="Enter username" name='username' onChange={onUsernameChange}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email"  name='email' onChange={onEmailChange}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Contact Number</Form.Label>
        <Form.Control type="Number" placeholder="Number" name='number' onChange={onNumberChange} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        
      </Form.Group>
      <Button variant="primary" type="submit" className='AddEmployeeButton' onSubmit={handleSubmit} >
        Submit
      </Button>
    </Form>
      
    </div>
  )
}

export default AddEmployee
