import axios from 'axios'
import { useEffect, useState } from 'react'
import './profile.css'
axios.defaults.withCredentials = true

const Profile = () => {
    const [user, setUser] = useState()
    const sendRequest = async () =>{
       const res = await axios.get('http://localhost:7000/api/user/profile',{
            withCredentials: true
        }) .catch((err) => console.log(err));
        const data = await res.data;
        return data;
    }

    useEffect(() =>{
        sendRequest().then((data) => setUser(data.user))
    },[])
    return (
        <div className='UserProfileContainer' >
            <div className='UserProfileForm'>
                <div className='UserProfileLeft UserProfileFormContainer'>
                    
                        <h1 className='UserProfileFormHeading'>USER PROFILE</h1>
                        {/* {user && <h1>{user.name}</h1> */}
                        
    }
                        
                      {user && <h3 className='UserProfileInput'>Name:{user.name}</h3>

                       }  
                        {/* <h3 className='UserProfileInput'>{user.email}</h3> */}
                       
                    
                </div>
            </div>
        </div>
    )
}

export default Profile
