import { useEffect } from "react";
import { Table, Button, Container } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import axios from 'axios'
import './listEmployee.css'
import { addEmployee } from "../EmployeeSlice";

const ListEmployee = () => {
    const dispatch = useDispatch();
    const employees = useSelector((state) => state.counter.employees)

    useEffect(()=>{
        console.log()
        if(employees.length === 0) 
        axios.get('http://localhost:7000/api/employee').then((response) => {
            console.log(response.data)
        //    if(employees.length !== 0) 
            dispatch(addEmployee(response.data))
        }).catch((error)=>{
            alert(error)
        })
    },[])
    return (
        <Container>
            {console.log('employee list',employees)}
            <div >
                <h3 className="EmployeeListTitle">Employee List</h3>
                <Link to="/addemployee" className="AddEmployeeLink"><Button>Add Employee</Button></Link>
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {employees? employees.map((index,employee)=>{
                            <tr>
                           <td>{index + 1}</td> 
                            <td>{employee.name}</td>
                            <td>{employee.username}</td>
                            <td>{employee.email}</td>
                            <td>{employee.phone}</td>
                            <td><Button>Edit</Button> <Button>Delete</Button></td>
                        </tr>
                       }):null} 
                        
                    </tbody>
                </Table>
            </div>
        </Container>
    )
}

export default ListEmployee
