import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom';
import { updateEmployee } from '../EmployeeSlice';
import { useState } from 'react';
import './updateEmployee.css'

const UpdateEmployee = () => {

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const params = useParams();
  

  const employees = useSelector((state)=> state.employee.employee)
  console.log( employees, params.id)
  const existingEmployee = employee.filter(employee => employee.id === params.id)
 console.log(existingEmployee)
  const {name, username,email,phone} = existingEmployee[0];
  const [values, setValues] = useState({
   // name:'', username:'', email:'', phone:''
  name, username, email,phone

  })

  const handleUpdateEmployee = (e) =>{
    e.preventDefault();
    setValues({name:'',username:'', email:'',phone:''})
    dispatch(updateEmployee([{
     _id:params.id, 
      name: values.name,
      username: values.username,
      email: values.email,
      phone: values.phone
    }]))
    console.log('user details',dispatch);
    axios.patch('http://localhost:7000/api/employee/${_id}',{
      
      _id: params.id,
      name: values.name,
      username: values. username,
      email: values.email,
      phone: values.phone
    }).then((response) => {
      alert('Successfully updated!', response)
      navigate('/listemployee')
    })
  }
  return (
    <div className='AddEmployeeContainer' >
      <div className='AddEmployeeForm'>
        <div className='AddEmployeeLeft'>
          <form className='AddEmployeeFormContainer' >
            <h1 className='AddEmployeeFormHeading'>UPDATE EMPLOYEE</h1>
            <input type="text"
              placeholder="Name"
              name="name"
              className='AddEmployeeInput'
              value={values.name}
              onChange={(e)=>setValues({...values, name: e.target.name})}
            />
            <input type="text"
              placeholder="Username"
              name="username"
              className='AddEmployeeInput'
             value={values.username}
             onChange={(e)=> setValues({...values,username: e.target.username})}
            />
            <input type="email"
              placeholder="Email"
              name="email"
              className='AddEmployeeInput'
              value={values.email}
              onChange={(e) => setValues({...values,email: e.target.email})}
            />

            <input type="number"
              placeholder="Contact Number"
              name="phone"
              className='AddEmployeeInput'
              value={values.phone}
              onChange={(e) => setValues({...values,phone: e.target.phone})}
            />
            <button className='AddFromLeftButton' onClick={handleUpdateEmployee}>Update</button>
          </form>
        </div>
      </div>
    </div>
  )
}

export default UpdateEmployee
